import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProjectForm = ({ projectId, onSubmitSuccess }) => {
  const [project, setProject] = useState({
    name: '',
    description: '',
    status: 'Not Started',
    owner: '',
    start_date: '',
    end_date: '',
    progress: 0,
    parent_pillar: '',
    priority: 1,
    budget: 0
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (projectId) {
      fetchProject();
    }
  }, [projectId]);

  const fetchProject = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/projects/${projectId}`);
      setProject(response.data);
    } catch (error) {
      console.error('Error fetching project:', error);
      setError('Failed to fetch project details.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProject(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      if (projectId) {
        await axios.put(`http://localhost:3001/api/projects/${projectId}`, project);
      } else {
        await axios.post('http://localhost:3001/api/projects', project);
      }
      onSubmitSuccess();
    } catch (error) {
      console.error('Error saving project:', error);
      setError('Failed to save project. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      {error && <div className="text-red-500">{error}</div>}
      <div>
        <label htmlFor="name" className="block">Name</label>
        <input
          type="text"
          id="name"
          name="name"
          value={project.name}
          onChange={handleChange}
          required
          className="w-full px-2 py-1 border rounded"
        />
      </div>
      {/* Add other form fields here */}
      <button 
        type="submit" 
        className="bg-blue-500 text-white px-4 py-2 rounded"
        disabled={loading}
      >
        {loading ? 'Saving...' : (projectId ? 'Update Project' : 'Create Project')}
      </button>
    </form>
  );
};

export default ProjectForm;
