import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import Dashboard from './components/Dashboard';
import ProjectList from './components/ProjectList';
import GanttChart from './components/GanttChart';
import ProjectForm from './components/ProjectForm';

const App = () => {
  const [projects, setProjects] = useState([]);
  const [activeTab, setActiveTab] = useState('dashboard');

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/projects');
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const addProject = async (projectData) => {
    try {
      await axios.post('http://localhost:3001/api/projects', projectData);
      fetchProjects();
    } catch (error) {
      console.error('Error adding project:', error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Cogito IS - Internal Projects</h1>
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList>
          <TabsTrigger value="dashboard">Dashboard</TabsTrigger>
          <TabsTrigger value="projects">Projects</TabsTrigger>
          <TabsTrigger value="gantt">Gantt Chart</TabsTrigger>
          <TabsTrigger value="new-project">New Project</TabsTrigger>
        </TabsList>
        <TabsContent value="dashboard">
          <Dashboard projects={projects} />
        </TabsContent>
        <TabsContent value="projects">
          <ProjectList projects={projects} />
        </TabsContent>
        <TabsContent value="gantt">
          <GanttChart projects={projects} />
        </TabsContent>
        <TabsContent value="new-project">
          <ProjectForm onSubmit={addProject} />
        </TabsContent>
      </Tabs>
    </div>
  );
};

export default App;
