import React, { useState } from 'react';
import axios from 'axios';

const ProjectForm = ({ onSubmitSuccess }) => {
  const [project, setProject] = useState({
    date: new Date(),
    owner_first_name: '',
    owner_last_name: '',
    title: '',
    department: '',
    manager: '',
    priority: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProject(prev => ({ ...prev, [name]: value }));
  };

  const handleDateChange = (e) => {
    const { name, value } = e.target;
    setProject(prev => ({
      ...prev,
      date: new Date(
        name === 'year' ? value : prev.date.getFullYear(),
        name === 'month' ? value - 1 : prev.date.getMonth(),
        name === 'day' ? value : prev.date.getDate()
      )
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post('http://localhost:3001/api/projects', {
        ...project,
        owner: `${project.owner_first_name} ${project.owner_last_name}`,
        start_date: project.date.toISOString().split('T')[0],
        status: 'Not Started',
        progress: 0
      });
      console.log('Server response:', response.data);
      onSubmitSuccess();
    } catch (error) {
      console.error('Error saving project:', error.response ? error.response.data : error.message);
      setError(`Failed to save project. ${error.response ? error.response.data.error : error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <h2 className="text-xl font-semibold">Project Request Form</h2>
      {error && <div className="text-red-500">{error}</div>}

      <div className="flex space-x-4">
        <div>
          <label htmlFor="month" className="block">Today's Date</label>
          <select
            id="month"
            name="month"
            value={project.date.getMonth() + 1}
            onChange={handleDateChange}
            className="px-2 py-1 border rounded"
          >
            {[...Array(12)].map((_, i) => (
              <option key={i} value={i + 1}>{i + 1}</option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="day" className="block">&nbsp;</label>
          <select
            id="day"
            name="day"
            value={project.date.getDate()}
            onChange={handleDateChange}
            className="px-2 py-1 border rounded"
          >
            {[...Array(31)].map((_, i) => (
              <option key={i} value={i + 1}>{i + 1}</option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="year" className="block">&nbsp;</label>
          <select
            id="year"
            name="year"
            value={project.date.getFullYear()}
            onChange={handleDateChange}
            className="px-2 py-1 border rounded"
          >
            {[...Array(10)].map((_, i) => {
              const year = new Date().getFullYear() + i;
              return <option key={year} value={year}>{year}</option>;
            })}
          </select>
        </div>
      </div>

      <div>
        <label htmlFor="owner_first_name" className="block">Project Owner</label>
        <div className="flex space-x-4">
          <input
            type="text"
            id="owner_first_name"
            name="owner_first_name"
            placeholder="First Name"
            value={project.owner_first_name}
            onChange={handleChange}
            required
            className="w-full px-2 py-1 border rounded"
          />
          <input
            type="text"
            id="owner_last_name"
            name="owner_last_name"
            placeholder="Last Name"
            value={project.owner_last_name}
            onChange={handleChange}
            required
            className="w-full px-2 py-1 border rounded"
          />
        </div>
      </div>

      <div>
        <label htmlFor="title" className="block">Project Title</label>
        <input
          type="text"
          id="title"
          name="title"
          placeholder="Project Title"
          value={project.title}
          onChange={handleChange}
          required
          className="w-full px-2 py-1 border rounded"
        />
      </div>

      <div className="flex space-x-4">
        <div className="flex-1">
          <label htmlFor="department" className="block">Department</label>
          <input
            type="text"
            id="department"
            name="department"
            placeholder="Department"
            value={project.department}
            onChange={handleChange}
            required
            className="w-full px-2 py-1 border rounded"
          />
        </div>
        <div className="flex-1">
          <label htmlFor="manager" className="block">Manager</label>
          <input
            type="text"
            id="manager"
            name="manager"
            placeholder="Manager"
            value={project.manager}
            onChange={handleChange}
            required
            className="w-full px-2 py-1 border rounded"
          />
        </div>
      </div>

      <div>
        <label htmlFor="priority" className="block">Priority (1-5, 5 being critical)</label>
        <select
          id="priority"
          name="priority"
          value={project.priority}
          onChange={handleChange}
          required
          className="w-full px-2 py-1 border rounded"
        >
          <option value="">Choose Priority</option>
          {[1, 2, 3, 4, 5].map(p => (
            <option key={p} value={p}>{p}</option>
          ))}
        </select>
      </div>

      <button 
        type="submit" 
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50"
        disabled={loading}
      >
        {loading ? 'Submitting...' : 'Submit Project Request'}
      </button>
    </form>
  );
};

export default ProjectForm;
