import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';

const Dashboard = ({ projects }) => {
  const statusCounts = projects.reduce((acc, project) => {
    acc[project.status] = (acc[project.status] || 0) + 1;
    return acc;
  }, {});

  const today = new Date();
  const thirtyDaysFromNow = new Date(today.getTime() + 30 * 24 * 60 * 60 * 1000);
  const upcomingDeadlines = projects.filter(project => {
    const endDate = new Date(project.end_date);
    return endDate >= today && endDate <= thirtyDaysFromNow;
  });

  const averageDuration = projects.reduce((sum, project) => {
    const start = new Date(project.start_date);
    const end = new Date(project.end_date);
    return sum + (end - start) / (1000 * 60 * 60 * 24);
  }, 0) / projects.length;

  const overdueProjects = projects.filter(project => 
    new Date(project.end_date) < today && project.status !== 'Completed'
  ).length;

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      <Card>
        <CardHeader>
          <CardTitle>Project Status</CardTitle>
        </CardHeader>
        <CardContent>
          {Object.entries(statusCounts).map(([status, count]) => (
            <div key={status} className="flex justify-between">
              <span>{status}:</span>
              <span>{count}</span>
            </div>
          ))}
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Upcoming Deadlines</CardTitle>
        </CardHeader>
        <CardContent>
          <ul>
            {upcomingDeadlines.map(project => (
              <li key={project.id}>{project.name} - {project.end_date}</li>
            ))}
          </ul>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Quick Stats</CardTitle>
        </CardHeader>
        <CardContent>
          <p>Total Projects: {projects.length}</p>
          <p>Average Duration: {averageDuration.toFixed(1)} days</p>
          <p>Overdue Projects: {overdueProjects}</p>
        </CardContent>
      </Card>
    </div>
  );
};

export default Dashboard;
